import React from "react";
import { Button } from "react-bootstrap";
import {useState} from "react"


const ToggleButton = (props) => {
	const [tags, setTag] = useState(Array(props.buttons.length).fill(false));

	const onTagSet = (index) => {
		tags[index] = !tags[index];
		setTag([...tags]);
	};

	return (
		<div>
			{props.buttons.map((button, index) => {
				return (
					<Button key={index} onClick={() => onTagSet(index)} className={"button-" + (tags[index] ? "active" : "inactive")}>
						{button.text}
					</Button>
				);
			})}
		</div>
	);
};

export default ToggleButton;
