import NavigationBar from "../navigation/NavigationBar";
import Container from "../util/Container";
import TextField from "@mui/material/TextField";

import { Button } from "react-bootstrap";
import React, { useState } from "react";

import "./Schematic.css";

const func = (input) => {
	return <li key={input.id}>{input.text}</li>;
};

const buttons = [
	{ text: "size", value: ["small", "medium", "big", "THICK"] },
	{ text: "type", value: ["core", "ore", "remote"] },
];

const Schematic = () => {
	const [inputText, setInputText] = useState([{ id: "1", text: "Hoho" }]);
	const handleInput = (e) => {
		setInputText(obj);
	};

	const [tags, setTag] = useState(Array(buttons.length).fill(false));

	const onTagSet = (index) => {
		tags[index] = !tags[index];
		setTag([...tags]);
	};

	return (
		<div className="schematic">
			<NavigationBar />
			<div className="search-bar">
				<div id="search">
					<TextField
						variant="outlined"
						label="Search for schematic"
						onChange={handleInput}
						inputProps={{ style: { width: "400px" } }}
					/>
					<Container input={inputText} func={func} />
				</div>
				<div>
					{buttons.map((button, index) => {
						return (
							<Button key={index} onClick={() => onTagSet(index)} className={"button-" + (tags[index] ? "active" : "inactive")}>
								{button.text }
							</Button>
						);
					})}
				</div>
			</div>
		</div>
	);
};

const obj = [
	{
		id: 1,
		text: "Devpulse",
	},
	{
		id: 2,
		text: "Linklinks",
	},
	{
		id: 3,
		text: "Centizu",
	},
	{
		id: 4,
		text: "Dynabox",
	},
	{
		id: 5,
		text: "Avaveo",
	},
	{
		id: 6,
		text: "Demivee",
	},
	{
		id: 7,
		text: "Jayo",
	},
	{
		id: 8,
		text: "Blognation",
	},
	{
		id: 9,
		text: "Podcat",
	},
	{
		id: 10,
		text: "Layo",
	},
];

export default Schematic;
