import React from "react";

const buttons = [
	{ text: "size", value: ["small", "medium", "big", "THICK"] },
	{ text: "type", value: ["core", "ore", "remote"] },
];

const ToggleButton = () => {
	const [tags, setTag] = useState(Array(buttons.length).fill(false));

	const onTagSet = (index) => {
		tags[index] = !tags[index];
		setTag([...tags]);
	};

	return (
		<div>
			{buttons.map((button, index) => {
				return (
					<Button key={index} onClick={() => onTagSet(index)} className={"button-" + (tags[index] ? "active" : "inactive")}>
						{button.text}
					</Button>
				);
			})}
		</div>
	);
};

export default ToggleButton;
