import React from "react";
import { useState } from "react";

import "./ToggleButton.css";
import ContextMenuButton from "./ContextMenuButton";

const ToggleButton = (props) => {
	const [toggleButtons, setToggleButtons] = useState(Array(props.buttons.length).fill({ show: false, active: -1 }));
	const [activeButton, setActive] = useState(-1);

	const onTagSet = (index) => {
		toggleButtons[index].show = !toggleButtons[index].show;
		setToggleButtons([...toggleButtons]);
	};

	return (
		<div>
			{props.buttons.map((button, index) => {
				return (
					<ContextMenuButton
						key={index}
						onClick={() => onTagSet(index)}
						setActive={setActive}
						className={"button-" + (toggleButtons[index].show ? "active" : "inactive")}
						buttons={button.value}
					>
						{button.text}
					</ContextMenuButton>
				);
			})}
		</div>
	);
};

export default ToggleButton;
