import NavigationBar from "../util/navigation/NavigationBar";
import Container from "../util/container/Container";
import ToggleButton from "../util/button/ToggleButton";

import React, { useState } from "react";

import "./Schematic.css";
import SearchIcon from "../util/Icon";

const buttons = [
	{ name: "Size", value: ["Small", "Medium", "Big", "THICK"] },
	{ name: "Type", value: ["Core", "Ore", "Remote"] },
	{ name: "Planet", value: ["Serpulo", "Erekir"] },
	{ name: "Rank", value: ["Ascendant", "Descendant"] },
];

const buildResult = ({ authorName, likes, dislike, schematicPath }, index) => {
	return (
		<div key={index} className="schematic-preview">
			<img className="schematic-img" src={schematicPath} alt="Not found"></img>
			<div className="schematic-description">
				Author name: {authorName}
				<br />
				Like: {likes}
				<br />
				Dislike: {dislike}
				<br />
			</div>
		</div>
	);
};

const Schematic = () => {
	const [inputText, setInputText] = useState([
		{
			authorName: "Shar",
			likes: 0,
			dislike: 0,
			schematicPath: "https://lh3.googleusercontent.com/izWFN-PyWnB6CswfwsgKsAG5gCQcLb5tq-YWeD8uI0vgZFHgBoZxPBntT17JLDuyD0t83g=s85",
		},
	]);
	const handleInput = (e) => {
		setInputText([
			{
				authorName: "Shar",
				likes: 0,
				dislike: 0,
				schematicPath: "https://lh3.googleusercontent.com/izWFN-PyWnB6CswfwsgKsAG5gCQcLb5tq-YWeD8uI0vgZFHgBoZxPBntT17JLDuyD0t83g=s85",
			},
			{
				authorName: "Shar",
				likes: 0,
				dislike: 0,
				schematicPath: "https://lh3.googleusercontent.com/izWFN-PyWnB6CswfwsgKsAG5gCQcLb5tq-YWeD8uI0vgZFHgBoZxPBntT17JLDuyD0t83g=s85",
			},
			{
				authorName: "Shar",
				likes: 0,
				dislike: 0,
				schematicPath: "https://lh3.googleusercontent.com/izWFN-PyWnB6CswfwsgKsAG5gCQcLb5tq-YWeD8uI0vgZFHgBoZxPBntT17JLDuyD0t83g=s85",
			},
		]);
	};

	return (
		<div className="schematic">
			<NavigationBar />
			<div className="search-bar-container">
				<div>
					<SearchIcon />
					<input className="search-bar" title="Text" placeholder="Schematic name" onChange={handleInput}></input>
				</div>
				<div className="tag-container">
					{buttons.map((button, index) => {
						return <ToggleButton key={index} button={button} />;
					})}
				</div>
			</div>
			<div className="schematic-container">
				<Container input={inputText} func={buildResult} />
			</div>
		</div>
	);
};

export default Schematic;
