import React from "react";
import { useState } from "react";

import ContextMenuButton from "./ContextMenuButton";

import "./ToggleButton.css";
import { Button } from "react-bootstrap";

const ToggleButton = (props) => {
	const [toggleButtons, setToggleButtons] = useState({ show: false, active: 0 });
	const [activeButton, setActive] = useState(-1);

	const onTagSet = (index) => {
		toggleButtons[index].show = !toggleButtons[index].show;
		setToggleButtons([...toggleButtons]);
	};

	return (
		<div>
			{props.buttons.map((button, index) => {
				return (
					<div>
						<Button key={index}>{button.text}</Button>
						{toggleButtons[index].active && (
							<ContextMenuButton
								key={index}
								onClick={() => onTagSet(index)}
								setActive={setActive}
								buttons={button.value}
								className={"toggle-button-" + (toggleButtons[index].show ? "active" : "inactive")}
							>
								{button.text}
							</ContextMenuButton>
						)}
					</div>
				);
			})}
		</div>
	);
};

export default ToggleButton;
